import React, { useEffect, useState } from 'react';
import nflScheduleData from '../scraping/nfl_schedule.json'; // Import the JSON file

const Content = ({ activeTab }) => {
  const [scheduleData, setScheduleData] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState('Week'); // Initialize with "Week"

  useEffect(() => {
    // Use the imported JSON data directly
    setScheduleData(nflScheduleData);
  }, [activeTab]);

  const handleWeekChange = (event) => {
    setSelectedWeek(event.target.value);
  };

  const filteredScheduleData = selectedWeek === 'Week' ? scheduleData : scheduleData.filter((game) => game.Week === selectedWeek);

  if (activeTab === 'NFL') {
    return (
      <div className="nfl-schedule">
        <div className="week-selector">
          <label htmlFor="weekSelect">Select Week:</label>
          <select id="weekSelect" onChange={handleWeekChange} value={selectedWeek}>
            <option value="Week">Week</option>
            {/* You can dynamically generate week options based on your data */}
            {Array.from(new Set(scheduleData.map((game) => game.Week))).map((week, index) => (
              <option key={index} value={week}>
                {week}
              </option>
            ))}
          </select>
        </div>
        <table>
          <thead>
            <tr>
              <th>Week</th>
              <th>Day</th>
              <th>Date</th>
              <th>Time</th>
              <th>Winner/tie</th>
              <th>Loser/tie</th>
            </tr>
          </thead>
          <tbody>
            {filteredScheduleData.map((game, index) => (
              <tr key={index}>
                <td>{game.Week}</td>
                <td>{game.Day}</td>
                <td>{game.Date}</td>
                <td>{game.Time}</td>
                <td>{game.TeamNames1}</td>
                <td>{game.TeamNames2}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }

  return null;
};

export default Content;
