// src/components/Content.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Content = ({ activeTab }) => {
  const [scheduleData, setScheduleData] = useState([]);

  useEffect(() => {
      axios
        .get('https://site.api.espn.com/apis/site/v2/sports/football/nfl/scoreboard')
        .then((response) => {
          // Extract the schedule data from the API response
          const schedule = response.data.events;
          setScheduleData(schedule);

          // You may need to adjust how you determine the current week here
          // For example, if there's a field in the response indicating the current week:
          // const currentWeek = response.data.currentWeek;
          // setCurrentWeek(currentWeek);
        })
        .catch((error) => {
          console.error('Error fetching NFL schedule data:', error);
        });

  }, [activeTab]);

  if (activeTab === 'NFL') {
    return (
      <div className="nfl-schedule">
        <div className="matchup-grid">
          {scheduleData.map((game) => (
            <div key={`${game.id}-${game.shortName}`} className="matchup-box">
              <p className="matchup-name">{game.shortName}</p>
              {/* Add more game information here */}
            </div>
          ))}
        </div>
      </div>
    );
  }

  return null;
};

export default Content;
