import React, { useEffect, useState } from 'react';
import nflScheduleData from '../scraping/nfl_schedule.json'; // Import the JSON file

const Content = ({ activeTab }) => {
  const [scheduleData, setScheduleData] = useState([]);

  useEffect(() => {
    // Use the imported JSON data directly
    setScheduleData(nflScheduleData);
  }, [activeTab]);

  if (activeTab === 'NFL') {
    return (
      <div className="nfl-schedule">
        <div className="matchup-grid">
          {scheduleData.length > 0 ? (
            scheduleData.map((game, index) => (
              <div key={index} className="matchup-box">
                <p className="matchup-name">{game.shortName}</p>
                {/* Add more game information here */}
              </div>
            ))
          ) : (
            <p>No NFL schedule data available.</p>
          )}
        </div>
      </div>
    );
  }

  return null;
};

export default Content;
