// src/components/Content.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Content = ({ activeTab }) => {
  const [scheduleData, setScheduleData] = useState([]);

  useEffect(() => {
    if (activeTab === 'NFL') {
      axios
        .get('http://api.sportradar.us/nfl/official/trial/v7/en/games/2022/REG/1/schedule.json?api_key=tv3ecnuq843a9vkrnm5pd5yz')
        .then((response) => {
          // Extract the schedule data from the API response
          console.log(response)
          const games = response.data.weeks[0].games; // Assuming all games are in the first week
          setScheduleData(games);
        })
        .catch((error) => {
          console.error('Error fetching NFL schedule data:', error);
        });
    }
  }, [activeTab]);

  if (activeTab === 'NFL') {
    return (
      <div className="nfl-schedule">
        <h2>NFL Schedule 2022-2023</h2>
        <ul>
          {scheduleData.map((game) => (
            <li key={game.id}>
              <p>Date: {game.scheduled}</p>
              <p>Teams: {game.home.name} vs {game.away.name}</p>
              {/* Add more game information here */}
            </li>
          ))}
        </ul>
      </div>
    );
  }

  return null;
};

export default Content;
