// src/components/Content.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Content = ({ activeTab }) => {
  const [scheduleData, setScheduleData] = useState([]);

  useEffect(() => {
    axios
        .get('https://site.api.espn.com/apis/site/v2/sports/football/nfl/scoreboard')
        .then((response) => {
          // Extract the schedule data from the API response
          const schedule = response.data.events;
          setScheduleData(schedule);
        })
        .catch((error) => {
          console.error('Error fetching NFL schedule data:', error);
        });

    if (activeTab === 'NFL') {
      return (
        <div className="nfl-schedule">
          <h2>NFL Schedule 2023-2024</h2>
          <ul>
            {scheduleData.map((game) => (
              <li key={game.id}>
                <p>Date: {game.date}</p>
                <p>Teams: {game.competitions[0].competitors[0].team.displayName} vs {game.competitions[0].competitors[1].team.displayName}</p>
                {/* Add more game information here */}
              </li>
            ))}
          </ul>
        </div>
      );
    }

    if (activeTab === 'Blog') {

    }
  }, [activeTab]);

  return null;
};

export default Content;
